#ifdef XSPICE .cm_func = NULL, .num_conn = 0, .conn = NULL, .num_param = 0, .param = NULL, .num_inst_var = 0, .inst_var = NULL, #endif },
.DEVparam = $(module)par, .DEVmodParam = $(module)mParam, .DEVload = $(module)load, .DEVsetup = $(module)setup, .DEVunsetup = $(module)unsetup, .DEVpzSetup = $(module)setup, .DEVtemperature = $(module)temp, .DEVtrunc = $(module)trunc, .DEVfindBranch = NULL, .DEVacLoad = $(module)acLoad, .DEVaccept = NULL, .DEVdestroy = $(module)destroy, .DEVmodDelete = $(module)mDelete, .DEVdelete = $(module)delete, .DEVsetic = NULL, .DEVask = $(module)ask, .DEVmodAsk = $(module)mAsk, .DEVpzLoad = $(module)pzLoad, .DEVconvTest = NULL, .DEVsenSetup = NULL, .DEVsenLoad = NULL, .DEVsenUpdate = NULL, .DEVsenAcLoad = NULL, .DEVsenPrint = NULL, .DEVsenTrunc = NULL, .DEVdisto = NULL, .DEVnoise = NULL, .DEVsoaCheck = NULL, .DEVinstSize = &$(module)iSize, .DEVmodSize = &$(module)mSize,
#ifdef CIDER .DEVdump = NULL, .DEVacct = NULL, #endif };
SPICEdev * get_$(module)_info(void) { return &$(module)info; }
